create table country
(
    id              bigint auto_increment
        primary key,
    iso2_code       varchar(16)  not null,
    iso3_code       varchar(16)  null,
    eng_name        varchar(128) null,
    full_eng_name   varchar(128) null,
    chn_name        varchar(128) null,
    full_chn_name   varchar(128) null,
    language        varchar(64)  null,
    phone_code      varchar(64)  null,
    capital         varchar(64)  null,
    currency        varchar(64)  null,
    native          varchar(128) null,
    emoji           varchar(64)  null,
    emojiU          varchar(128) null,
    wiki_data_id    varchar(64)  null,
    continent_id    bigint       null,
    description     longtext     null,
    is_delete       tinyint(1)   not null,
    create_datetime datetime(6)  not null,
    create_by       varchar(32)  not null,
    update_datetime datetime(6)  not null,
    update_by       varchar(32)  not null,
    constraint iso2_code
        unique (iso2_code),
    constraint country_continent_id_ce4fdf4e_fk_continent_id
        foreign key (continent_id) references continent (id)
);

create index country_create_datetime_a5cd7d36
    on country (create_datetime);

create index country_update_datetime_d763e1e7
    on country (update_datetime);

